import React, {Component} from 'react';
import Axios from 'axios';

class Create extends Component
{
    constructor(props)
    {
        super(props);
        this.state = 
        {
            username: "",
            firstname: "",
            lastname: "",
            phone_number: "",
            password: ""
        }

        this.usernameHandler = this.usernameHandler.bind(this);
        this.firstnameHandler = this.firstnameHandler.bind(this);
        this.lastnameHandler = this.lastnameHandler.bind(this);
        this.phone_numberHandler = this.phone_numberHandler.bind(this);
        this.passwordHandler = this.passwordHandler.bind(this);
        this.submitCreate = this.submitCreate.bind(this);
    }

    usernameHandler = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    firstnameHandler = (e) => {
        this.setState({
            firstname: e.target.value
        })
    }

    lastnameHandler = (e) => {
        this.setState({
            lastname: e.target.value
        })
    }

    phone_numberHandler = (e) => {
        this.setState({
            phone_number: e.target.value
        })
    }

    passwordHandler = (e) => {
        this.setState({
            password: e.target.value
        })
    }

    submitCreate = (e) => {
        const data = {
            username: this.state.username,
            firstname: this.state.firstname,
            lastname: this.state.lastname, 
            phone_number: this.state.phone_number,
            password: this.state.password
        }

        Axios.post('http://localhost:3001/create', data);
    }

    render(){
        return(
            <div>
                <h2>Create a New User</h2>

                <br />
                <div class = "container">
                    <form>
                        <div style = {{width: '30%'}} class = "form-group">
                            Username: <br />
                            <input onChange = {this.usernameHandler} type = "text" class = "form-control" name = "username" placeholder = "Username" required />
                        </div>

                        <div style = {{width: '30%'}} class = "form-group">
                            <br />First name: <br />
                            <input onChange = {this.firstnameHandler} type = "text" class = "form-control" name = "firstname" placeholder = "Firstname" required />   
                        </div>

                        <div style = {{width: '30%'}} class = "form-group">
                            <br />Last name: <br />
                            <input onChange = {this.lastnameHandler} type = "text" class = "form-control" name = "lastname" placeholder = "Lastname" required />
                        </div>

                        <div style = {{width: '30%'}} class = "form-group">
                            <br />Phone number: <br />
                            <input onChange = {this.phone_numberHandler} type = "text" class = "form-control" name = "phone_number" placeholder = "Phone number" required />
                        </div>

                        <div style = {{width: '30%'}} class = "form-group">
                            <br />Password: <br />
                            <input onChange = {this.passwordHandler} type = "password" class = "form-control" name = "password" placeholder = "Password" required />
                        </div>

                        <div style = {{width: '30%'}}>
                            <br />
                            <button class = "btn btn-success" type = "submit" onClick = {this.submitCreate}>Create New User</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

export default Create;
